package CentricToAll1;

public class Lab018 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//Logical operators
		
		int a=120;
		int b=25;
		System.out.println(a>b && b<a);//Both the conditions should be true, to get true
	    System.out.println(a>b || b>a);//Any of the one condition should be  true.
		
		
		//Bitwise operators , same logic for bitwise operators as well
		
		int c=125;
		int d=40;
		
		System.out.println(c<d & d<c);
		System.out.println(c>d | d>c);
		

	}

}
